<?phpdeclare(strict_types=1);namespace App\Service;use Symfony\Contracts\HttpClient\HttpClientInterface;class StarWarsApiService{    private const BASE_URL = 'https://swapi.py4e.com/api/people/';    private const ITEM = 'ITEM';    private const COLLECTION = 'COLLECTION';    public function __construct(        private readonly HttpClientInterface $httpClient    ) {    }    public function getPersonnage(int $id = null): array    {        return $this->makeRequest(self::ITEM, $id);    }    public function getPersonnages(): array    {        return $this->makeRequest(self::COLLECTION);    }    private function makeRequest(string $type, ?int $id = null): array    {        $url = $id ? self::BASE_URL . $id : self::BASE_URL;        $response = $this->httpClient->request('GET', $url);        $data = match($type) {            self::ITEM => $response->toArray(),            self::COLLECTION => $response->toArray()['results'],        };        return $data;    }}